import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/a'; // Adjust to your backend URL if necessary

// Register User
export const register = async (data) => axios.post(`${API_BASE_URL}/auth/register`, data);

// Login User
export const login = async (data) => axios.post(`${API_BASE_URL}/auth/login`, data);

// Verify OTP
export const verifyOtp = async (data) => axios.post(`${API_BASE_URL}/auth/verify-otp`, data);
