import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api'; // Adjust if needed

// Register API call
export const register = async (data) => {
    console.log('Sending register request:', data); // Debug the data being sent
    return axios.post(`${API_BASE_URL}/auth/register`, data)
        .then((response) => {
            console.log('Register response:', response.data); // Debug response
            return response;
        })
        .catch((error) => {
            console.error('Register error response:', error.response?.data || error.message); // Debug errors
            throw error;
        });
};

// Login User
export const login = async (data) => axios.post(`${API_BASE_URL}/auth/login`, data);

// Verify OTP
export const verifyOtp = async (data) => axios.post(`${API_BASE_URL}/auth/verify-otp`, data);
