import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api';

export const login = (data) => axios.post(`${API_BASE_URL}/auth/login`, data);

export const register = (data) =>
  axios.post(`${API_BASE_URL}/auth/register`, data);

export const sendOtp = (data) => axios.post(`${API_BASE_URL}/send-otp`, data);

export const verifyOtp = (data) => axios.post(`${API_BASE_URL}/verify-otp`, data);
